content.text:
/* Element selector */
p { color: black; }

/* Class selector */
.button { padding: 1rem; }

/* ID selector */
#header { background: #eee; }

/* Attribute selector */
input[type="email"] { border-color: blue; }

/* First child */
ul li:first-child {}

/* Even rows */
tr:nth-child(even) {}

/* NOT selector */
button:not(.primary) {}

/* Adjacent sibling */
h1 + p {}

color: red;
color: #ff0000;
color: rgb(255, 0, 0);
color: hsl(0, 100%, 50%);

font-size: 1rem;
font-weight: 600;
line-height: 1.5;
font-family: "Inter", sans-serif;

padding: 1rem;
margin: 2rem;
border: 1px solid #ccc;
box-sizing: border-box;

display: block;
display: inline;
display: inline-block;
display: flex;
display: grid;

display: flex;
justify-content: center;
align-items: center;
gap: 1rem;

display: grid;
grid-template-columns: repeat(3, 1fr);
gap: 1rem;

/* Centered container */
.container {
  max-width: 800px;
  margin: 0 auto;
  padding: 1rem;
}

/* Sidebar layout */
.layout {
  display: grid;
  grid-template-columns: 250px 1fr;
  gap: 2rem;
}

/* Card grid */
.cards {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
}

position: static;
position: relative;
position: absolute;
position: fixed;
position: sticky;

transition: all 0.3s ease;

button:hover {
  transform: scale(1.05);
}

@keyframes fade {
  from { opacity: 0; }
  to { opacity: 1; }
}

.element {
  animation: fade 1s ease-in-out;
}

@media (max-width: 600px) {
  .container {
    padding: 1rem;
  }
}

/* Fluid typography */
h1 {
  font-size: clamp(2rem, 5vw, 4rem);
}

/* Container queries */
@container (min-width: 500px) {
  .card {
    flex-direction: row;
  }
}

:root {
  --primary: #4f46e5;
}

button {
  background: var(--primary);
}
